class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue< pair<int,int> > pq;
        int h0=0;
        int v1=0;
        long long  ans=0;
        for(auto e:h){
            pq.push({e,0});
        }
        for(auto e:v){
            pq.push({e,1});
        }
        while(pq.size()){
            int i=pq.top().second;
            int v=pq.top().first;
            pq.pop();
            if(i==0){
               ans+= (v1==0?1:v1)*1ll*v;
               if(h0==0) h0=2;
               else  h0++;
            }
            else{
                ans+= (h0==0?1:h0)*1ll*v;
               if(v1==0) v1=2;
               else  v1++;
            }
        }
        return ans;

    }
};
