class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        reverse(h.begin(),h.end());
        reverse(v.begin(),v.end());
        long long i=0,j=0,k=0,k1=0;
        m--;
        n--;
        for(i=0,j=0;i<m&&j<n;){
            if(h[i]>v[j]){
                ans+=(h[i]*(k1+1));
                k++;
                i++;
            }
            else{
                ans+=(v[j]*(k+1));
                k1++;
                j++;
            }
        }
        long long tf=0;
        while(i<m){
            tf+=h[i];
            i++;
        }
        ans+=tf*(k1+1);
        tf=0;
        while(j<n){
            tf+=v[j];
            j++;
        }
        ans+=tf*(k+1);
        return ans;
    }
};
