class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        total_cost = 0
        horizontal_pieces = 1 
        vertical_pieces = 1  

        cuts = []
        for cost in horizontalCut:
            cuts.append((cost, 'H'))
        for cost in verticalCut:
            cuts.append((cost, 'V'))

        # descending order
        cuts.sort(reverse=True, key=lambda x: x[0])

        # Apply each cut
        for cost, type in cuts:
            if type == 'H':
                total_cost += cost * vertical_pieces 
                horizontal_pieces += 1
            else:
                total_cost += cost * horizontal_pieces 
                vertical_pieces += 1

        return total_cost
