class Solution {
    public long minimumCost(int m, int n, int[] hCut, int[] vCut) {
        long res = 0;
        Arrays.sort(hCut);
        Arrays.sort(vCut);

        long hFac = 1;
        long vFac = 1;

        int i = hCut.length -1;
        int j = vCut.length -1;

        while(i>=0 && j>=0) {
            if(hCut[i]>=vCut[j]) {
                res += (long)(hCut[i]) * hFac;
                vFac++;
                i--;
            }
            else {
                res += (long)(vCut[j]) * vFac;
                hFac++;
                j--;
            }

        }

        while(i>=0) {
            res += (long)(hCut[i]) * hFac;
            vFac++;
            i--;
        }

        while(j>=0) {
            res += (long)(vCut[j]) * vFac;
            hFac++;
            j--;
        }

        return res;
    }
}
