class Solution {
public:
    bool static comp(int a, int b){
        return a>b;
    }
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ver = 1;
        long long hor = 1;
        long long minCost = 0; 
        sort(horizontalCut.begin(),horizontalCut.end(),comp);
        sort(verticalCut.begin(),verticalCut.end(),comp);
        int i=0;
        int j=0;
        while(i<m-1&&j<n-1){
            if(horizontalCut[i]>verticalCut[j]){
                minCost += (hor*horizontalCut[i]);
                ver++;
                i++;
            } else{
                minCost += (ver*verticalCut[j]);
                hor++;
                j++; 
            }
        }
        while(i<m-1){
            minCost += (hor*horizontalCut[i]);
            i++;
        }
        while(j<n-1){
            minCost += (ver*verticalCut[j]);
            j++;
        }
        return minCost;
    }
};
