class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCutCosts, vector<int>& verticalCutCosts) {
        vector<pair<int, bool>> cuts;
        for(int cost : horizontalCutCosts){
            cuts.push_back({cost, true});
        }
        for(int cost : verticalCutCosts){
            cuts.push_back({cost, false});
        }
        sort(cuts.rbegin(), cuts.rend());
        long long horizontalPieces = 1, verticalPieces = 1, totalCost = 0;
        for(auto cut : cuts){
            if(cut.second){
                totalCost += cut.first * verticalPieces;
                horizontalPieces++;
            } else {
                totalCost += cut.first * horizontalPieces;
                verticalPieces++;
            }
        }
        return totalCost;

    }
};
