typedef pair<int,int> PII;
typedef long long LL;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<PII,vector<PII>,less<PII>> Q;
       LL ans=0;
       for(int i=0;i<m-1;i++)
       {
            Q.push({horizontalCut[i],0});
       }
        for(int i=0;i<n-1;i++)
       {
            Q.push({verticalCut[i],1});
       }
        int row=1,col=1;
        while(!Q.empty())
        {
            auto [p,q]=Q.top();
            // cout<<p<<" "<<q<<" "<<row<<" "<<col<<" "<<"\n";
            Q.pop();
            if(q==0)
            {
                ans+=(LL)p*col;
                row++;
            }
            else
            {
                ans+=(LL)p*row;
                col++;
            }
        }
        return ans;
    }
};
