class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<ll, ll>> v;
      for (ll i = 0; i < m - 1; i++)
      {
        ll x=horizontalCut[i],y=1;
         v.push_back({x, y});
      }
      for (ll i = 0; i < n - 1; i++)
      {
        ll y=verticalCut[i],x=0;
         v.push_back({y, x});
      }
      sort(v.begin(), v.end(), greater<pair<ll, ll>>());

      ll hp = 1;
      ll vp = 1;
      ll ans = 0;

      for (auto it : v)
      {
         if (it.second)
         {
            ans += it.first * vp;
            hp++;
         }
         else
         {
            ans += it.first * hp;
            vp++;
         }
      }

      return ans;
    }
};
