class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horiCut, vector<int>& veriCut) {
        int hori=0,veri=0,horipi=1,veripi=1;
        long long ans=0;
        sort(horiCut.rbegin(),horiCut.rend());
        sort(veriCut.rbegin(),veriCut.rend());
        while(hori<m-1 && veri<n-1)
        {
            if(horiCut[hori]>=veriCut[veri])
            {
                ans+=horiCut[hori]*veripi;
                horipi++;
                hori++;
            }
            else{
                ans+=veriCut[veri]*horipi;
                veripi++;
                veri++;
            }
        }
        while(veri<n-1)
        {
                ans+=veriCut[veri]*horipi;
                veripi++;
                veri++;
        }
        while(hori<m-1)
        {
                ans+=horiCut[hori]*veripi;
                horipi++;
                hori++;

        }

        return ans;
    }
};
