class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(v.begin(),v.end(),greater<int>());
        sort(h.begin(),h.end(),greater<int>());
        long long hPieces = 1, vPieces = 1;
        long long cost = 0;
        long long a=0,b=0;
         while (a<h.size()&& b<v.size()) {
            if (v[b]<h[a]) {
                cost+= vPieces*h[a];
                a++;
                hPieces++;
            } else {
                cost += hPieces*v[b];
                b++;
                vPieces++;
            }
        }
        while (b < v.size()) {
            cost += v[b] * hPieces;
            b++;
        }
        while (a < h.size()) {
            cost += h[a] * vPieces;
            a++;
        }

        return cost;
    }
};
