class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long Hfactor = 1, Vfactor = 1, i = 0, j = 0, ans = 0;
        sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(), greater<int>());
        while(i < m-1 && j < n-1){
            if(h[i] > v[j]) {
                ans += h[i] * Vfactor;
                Hfactor++;
                i++;
            } else {
                ans += v[j] * Hfactor;
                Vfactor++;
                j++;
            }
        }
        while(i < m-1) {
            ans += h[i] * Vfactor;
            i++;
        }
        while(j < n-1) {
            ans += v[j] * Hfactor;
            j++;
        }
        return ans;
    }
};
