using ll = long long;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        // same as 3
        sort(begin(h),end(h), greater<int>());
        sort(begin(v),end(v), greater<int>());

        int hs = 1 , vs = 1;
        int ind1 = 0 , ind2 = 0;
        ll sum = 0;

        while (ind1 < h.size() && ind2 < v.size()) {
            if (h[ind1] >= v[ind2]) {
                sum += h[ind1] * vs;
                hs++;
                ind1++;
            } else {
                sum += v[ind2] * hs;
                vs++;
                ind2++;
            }
        }

        while (ind2 < v.size()) {
            sum += v[ind2] * hs;
            ind2++;
        }

        while (ind1 < h.size()) {
            sum += h[ind1] * vs;
            ind1++;
        }

        return sum;
    }
};
