public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        var cuts = new List<(int cost,char direction)>();
        foreach(var cut in horizontalCut){
            cuts.Add((cut,'H'));
        }
        foreach(var cut in verticalCut){
            cuts.Add((cut,'V'));
        }
        cuts.Sort((a,b)=>b.cost-a.cost);

        long h_pieces =1;
        long v_pieces=1;
        long total =0;
        foreach(var cut in cuts){
            if(cut.direction=='H'){
                total += cut.cost * v_pieces;
                h_pieces+=1;
            }else{
                total += cut.cost * h_pieces;
                v_pieces+=1;
            }
        }
        return total;
    }
}
