public class Solution 
{
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) 
    {
        List<(int el, bool horizontal)> sorted = new();
        foreach (var item in horizontalCut)
            sorted.Add((item, true));

        foreach (var item in verticalCut)
            sorted.Add((item, false));

        long totalHorizont = 1, totalVertical = 1, res = 0;

        foreach (var item in sorted.OrderByDescending((x) => x.el))
        {
            if (item.horizontal)
            {
                totalHorizont++;
                res += (long)item.el * totalVertical;
            }
            else
            {
                totalVertical++;
                res += (long)item.el * totalHorizont;
            }
        }

        return res;
    }
}
