class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        ll ans = 0;
        ll cnth = 1, cntv = 1, idx1 = 0, idx2 = 0;

        while (idx1 < horizontalCut.size() && idx2 < verticalCut.size()) {
            if (horizontalCut[idx1] > verticalCut[idx2]) {
                ans += horizontalCut[idx1] * cntv;
                cnth++;
                idx1++;
            } else {
                ans += verticalCut[idx2] * cnth;
                cntv++;
                idx2++;
            }
        }

        while (idx1 < horizontalCut.size()) {
            ans += horizontalCut[idx1] * cntv;
            idx1++;
        }

        while (idx2 < verticalCut.size()) {
            ans += verticalCut[idx2] * cnth;
            idx2++;
        }

        return ans;
    }
};
