class Solution(object):
    def minimumCost(self, m, n, a, b):
        tot=[]
        for i in range(len(a)):
            tot.append([a[i],1])

        for i in range(len(b)):
            tot.append([b[i],0])

        cuta=1
        cutb=1
        ans=0
        tot.sort(reverse=True)
        for i in range(len(tot)):
            if(tot[i][1]==1):
                ans+=(tot[i][0]*cutb)
                cuta+=1
            else:
                ans+=(tot[i][0]*cuta)
                cutb+=1
        return ans
