class Solution:
    def minimumCost(self, m: int, n: int, hcut: List[int], vcut: List[int]) -> int:
        x, y = 0,0
        heapx = []
        heapy = []
        res = 0
        for i in hcut:
            heappush(heapx, -i)
        for i in vcut:
            heappush(heapy, -i)

        while x<m-1 and y<n-1:
            if -heapx[0]<-heapy[0]:
                y+=1
                res += -heapy[0]*(x+1)
                heappop(heapy)
            else:
                x+=1
                res += -heapx[0]*(y+1)
                heappop(heapx)
        while heapx:
            res += -heappop(heapx)*(y+1)
        while heapy:
            res += -heappop(heapy)*(x+1)
        return res
