class Solution {
    public long minimumCost(int m, int n, int[] hCut, int[] vCut) {
        Arrays.sort(hCut);
        Arrays.sort(vCut);
        int i = m - 2, j = n - 2;
        long result = 0;
        while(i >= 0 || j >= 0) {
            if(i >= 0 && j >= 0) {
                if(hCut[i] > vCut[j]) {
                    result += (long)hCut[i] * (n - j - 1);
                    i--;
                }
                else {
                    result += (long)vCut[j] * (m - i - 1);
                    j--;
                }
            }
            else if(i >= 0) {
                result += (long)hCut[i] * (n - j - 1);
                i--;
            }
            else {
                result += (long)vCut[j] * (m - i - 1);
                j--;
            }
        }
        return result;
    }
}
