class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long nH = 0, nV = 0, i = 0;
        long long ans = 0;

        // {cost, 'H'}
        vector< pair<long long,char> >v;
        for(i=0; i<m-1; i++) v.push_back({horizontalCut[i],'H'});
        for(i=0; i<n-1; i++) v.push_back({verticalCut[i],'V'});
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());

        for(auto p: v){
            long long cost = p.first;
            char dir = p.second;
            if(dir == 'H') {
                ans += cost * (nV+1);
                nH++;
            }
            else {
                ans += cost * (nH+1);
                nV++;
            }
        }

        return ans;
    }
};
