class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        long long  h = 0, v = 0;
        long long hp = 1, vp = 1;
        long long t_cst = 0;

        while (h < hc.size() && v < vc.size()) {
            if (hc[h] > vc[v]) {
                t_cst += hc[h] * vp;
                hp++;
                h++;
            } else {
                t_cst += vc[v] * hp;
                vp++;
                v++;
            }
        }

        while (v < vc.size()) {
            t_cst += vc[v] * hp;
            v++;
        }

        while (h < hc.size()) {
            t_cst += hc[h] * vp;
            h++;
        }

        return t_cst;
    }
};
