class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        actions = [(c, 0) for c in horizontalCut] + [(c, 1) for c in verticalCut]
        actions.sort(reverse = True)

        ans = 0
        hn = 1
        vn = 1

        for c, t in actions:
            if t == 0:
                hn += 1
                ans += c * vn
            else:
                vn += 1
                ans += c * hn

        return ans
