class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        if(m==1){
            long res = 0;
            for(int i:verticalCut){
                res+=i;
            }
            return res;
        }
        if(n==1){
            long res = 0;
            for(int i:horizontalCut){
                res+=i;
            }
            return res;
        }
        int horNum = 1, verNum = 1;
        long res = 0;
        int p1 = horizontalCut.length-1, p2 = verticalCut.length-1;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        while(p1>=0&&p2>=0){
            if(horizontalCut[p1]>verticalCut[p2]){
                res+=horizontalCut[p1]*horNum;
                verNum++;
                p1--;
            }else{
                res+=verticalCut[p2]*verNum;
                horNum++;
                p2--;
            }
        }
        while(p1>=0){
            res+=horizontalCut[p1]*horNum;
            p1--;
        }
        while(p2>=0){
            res+=verticalCut[p2]*verNum;
            p2--;
        }
        return res;
    }
}
