class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []
        for num in horizontalCut:
            cuts.append((num, 'h'))
        for num in verticalCut:
            cuts.append((num, 'v'))
        cuts.sort(reverse=True)

        ans = vcuts = hcuts = 0
        for cost, typ in cuts:
            if typ == 'h':
                ans += (vcuts + 1) * cost
                hcuts += 1
            else:
                ans += (hcuts + 1) * cost
                vcuts += 1
        return ans
