class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        int row_c=1,col_c=1;
        ranges::sort(a.begin(),a.end(),greater<int>());
        ranges::sort(b.begin(),b.end(),greater<int>());
        int i=0,j=0;
        long long ans=0;
        while(i<m-1 && j<n-1){
            if(a[i]>b[j]||(a[i]==b[j]&&row_c>=col_c)){
                ans+=a[i]*col_c;
                row_c+=1;
                i++;
            } else {
                ans+=b[j]*row_c;
                col_c+=1;
                j++;
            }
            // cout << ans << endl;
        }
        // cout << 1 << endl;
        for(;i<m-1;i++){
            ans+=a[i]*col_c;
        }
        for(;j<n-1;j++){
            ans+=b[j]*row_c;
        }
        return ans;
    }
};
