class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin(),hor.end(),greater<int>());
        sort(ver.begin(),ver.end(),greater<int>());
        long long int h=1,v=1;
        long long int hindex=0,vindex=0;
        long long int cost=0;
        while(hindex<hor.size()||vindex<ver.size())  {
            if(hindex==hor.size()){
                    cost+=ver[vindex]*v;
                    h++;
                    vindex++;
            }
            else if(vindex==ver.size()){
                    cost+=hor[hindex]*h;
                    v++;
                    hindex++;
            }
            else{
                if(hor[hindex]>ver[vindex]){
                    cost+=hor[hindex]*h;
                    v++;
                    hindex++;
                }
                else{
                    cost+=ver[vindex]*v;
                    h++;
                    vindex++;
                }
            }
        }
        return cost;
    }
};
