class Solution:
    def minimumCost(self, m: int, n: int, horizontalCuts: List[int], verticalCuts: List[int]) -> int:
        combinedCuts = [(cost, 'H') for cost in horizontalCuts] + [(cost, 'V') for cost in verticalCuts]
        combinedCuts.sort(reverse=True, key=lambda x: x[0])
        totalCost = 0
        horizontalPieces, verticalPieces = 1, 1
        for cost, cutType in combinedCuts:
            if cutType == 'H':
                totalCost += cost * verticalPieces
                horizontalPieces += 1
            else:
                totalCost += cost * horizontalPieces
                verticalPieces += 1
        return totalCost
