class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.rbegin(),hCut.rend());
        sort(vCut.rbegin(),vCut.rend());
        long long rows=1;
        long long cols=1;
        long long ans=0;
        int i=0;
        int j=0;
        while(i<m-1 && j<n-1){
            if(hCut[i]>vCut[j]){
                ans+=(cols*hCut[i]);
                rows++;
                i++;
            }
            else{
                ans+=(rows*vCut[j]);
                cols++;
                j++;
            }
        }
        while(i<m-1){
            ans+=(cols*hCut[i]);
            rows++;
            i++;
        }
        while(j<n-1){
            ans+=(rows*vCut[j]);
            cols++;
            j++;
        }
        return ans;
    }
};
