class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>>cut;
        for(int i:horizontalCut)cut.push_back({i,0});
        for(int i:verticalCut)cut.push_back({i,1});
        sort(cut.begin(),cut.end());
        reverse(cut.begin(),cut.end());
        long ch=0,cv=0;
        long res=0;
        for(auto it:cut){
            if(it.second==1){
                res+=(long)((ch+1)*it.first);
                cv++;
            }
            else{
                res+=(long)((cv+1)*it.first);
                ch++;
            }
        }
        return res;
    }
};
