#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h_c, vector<int>& v_c) {
        long long i_count = 1,gin = 1;
        int i = 0, col = 0;
        long long res_ans = 0;

        sort(h_c.rbegin(), h_c.rend());
        sort(v_c.rbegin(), v_c.rend());

        while (i < (m - 1) && col < (n - 1)) {
            if (h_c[i] > v_c[col]) {
                res_ans +=gin * h_c[i];
                i_count++;
                i++;
            } else {
                res_ans += i_count * v_c[col];
               gin++;
                col++;
            }
        }

        while (i < (m - 1)) {
            res_ans +=gin * h_c[i];
            i_count++;
            i++;
        }
        while (col < (n - 1)) {
            res_ans += i_count * v_c[col];
           gin++;
            col++;
        }

        return res_ans;
    }
};
