class Solution {
public:
    long long minimumCost(int m, int n, vector<int>&h, vector<int>&v) {
        long long  ho = 1;
        long long  ve = 1;
        long long  i = 0;
        long long  k = 0;
        long long  c = 0;
          sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(),greater<int>());



        while (k<v.size() && i < h.size()) {
            if (h[i] >= v[k]) {
                c= c+ve*h[i];
                i++;
                ho++;
            } else {
                c = c+ho*v[k];
                ve++;
                k++;
            }
        }
          while (k < v.size()) {
            c= c+ho*v[k];
            k++;
           }
        while (i < h.size()) {
            c =c+ ve*h[i];
            i++;
        }

        return c;
    }
};
