class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        reverse(h.begin(), h.end());
        reverse(v.begin(), v.end());
        long long s1 = 0, s2 = 0;
        for(auto x : h) s1 += x;
        for(auto x : v) s2 += x;
        int i = 0, j = 0;
        long long res = 0;
        while(i < h.size() || j < v.size()){
            if(i >= h.size()){
                res += v[j] * (long long)(h.size() + 1);
                j ++;
            }
            else if(j >= v.size()){
                res += h[i] * (long long)(v.size() + 1);
                i ++;
            }
            else{
                if(h[i] > v[j]){
                    res += (long long)h[i] * (j + 1);
                    s1 -= h[i];
                    i ++;
                }
                else{
                    res += (long long)v[j] * (i + 1);
                    s2 -= v[j];
                    j ++;
                }
            }
        }
        return res;
    }
};
