class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int cntM=0;
        int cntN=0;
        long res=0;
        m-=2;
        n-=2;
        while(m>=0&&n>=0){
            if(horizontalCut[m]>=verticalCut[n]){
                res+=horizontalCut[m]*(cntN+1);
                cntM++;
                m--;
            }else{
                res+=verticalCut[n]*(cntM+1);
                cntN++;
                n--;
            }
        }
        while(m>=0){
            res+=horizontalCut[m]*(cntN+1);
            m--;
        }
        while(n>=0){
            res+=verticalCut[n]*(cntM+1);
            n--;
        }
        return res;
    }
}
