class Solution {
private:
    static bool comp(pair<long long,char>a, pair<long long,char>b)
    {
        if(a.first==b.first) return a.second<b.second;
        return a.first>b.first;
    }
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long,char>>v;
        for(int i=0;i<m-1;i++) v.push_back({((long long)(horizontalCut[i])),'H'});
        for(int i=0;i<n-1;i++) v.push_back({((long long)(verticalCut[i])),'V'});
        sort(v.begin(),v.end(),comp);

        long long verticalPiece=1;
        long long horizontalPiece=1;
        long long cost=0;

        for(int i=0;i<v.size();i++)
        {
            if(v[i].second=='V')
            {
                cost+= v[i].first*horizontalPiece;
                verticalPiece++;
            }
            if(v[i].second=='H')
            {
                cost+= v[i].first*verticalPiece;
                horizontalPiece++;
            }
        }

        return cost;
    }
};
