class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        List<int[]> cuts = new ArrayList<>();

        for (int cost : horizontalCut) {
            cuts.add(new int[]{cost, 0});
        }

        for (int cost : verticalCut) {
            cuts.add(new int[]{cost, 1});
        }

        cuts.sort((a, b) -> b[0] - a[0]);

        int horizontalPieces = 1;
        int verticalPieces = 1;
        long totalCost = 0;

        for (int[] cut : cuts) {
            if (cut[1] == 0) {
                totalCost += cut[0] * verticalPieces;
                horizontalPieces++;
            } else {
                totalCost += cut[0] * horizontalPieces;
                verticalPieces++;
            }
        }

        return totalCost;
    }
}
