class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        a = []
        for i in range(m-1):
            a.append((horizontalCut[i],'h',i+1))

        for i in range(n-1):
            a.append((verticalCut[i],'v',i+1))

        a.sort(reverse = True)

        v = h = 1
        tot = 0
        for cost, t, _ in a:
            if t == 'h':
                tot+=cost*h
                v+=1
            else:
                tot+=cost*v
                h+=1

        return tot
