class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        sort(hcut.begin(), hcut.end(), greater<int>());
        sort(vcut.begin(), vcut.end(), greater<int>());

        int i = 0, j = 0;
        long long x = 0, temp;
        while(i < hcut.size() && j < vcut.size())
        {
            if(hcut[i] > vcut[j])
            {
                temp = hcut[i];
                temp *= (j+1);
                x += temp;
                i++;
            }
            else{
                temp = vcut[j];
                temp *= (i+1);
                x += temp;
                j++;
            }
        }
        while(i != hcut.size())
        {
            temp = hcut[i];
            temp *= (j+1);
            x += temp;
            i++;
        }
        while(j != vcut.size())
        {
            temp = vcut[j];
            temp *= (i+1);
            x += temp;
            j++;
        }
        return x;        
    }
};
