class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        vector<pair<int, int>> v;
        long long ans = 0;
        int mulh = 1, mulv = 1;
        for(int i = 0; i < m - 1; i++){
            v.push_back({hor[i], 1});
        }
        for(int i = 0; i < n - 1; i++){
            v.push_back({ver[i], 0});
        }
        // 1 hor, 0 ver
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        for(int i = 0; i < m + n - 2; i++){
            if(v[i].second == 1){
                ans += v[i].first * 1LL * mulh;
                mulv++;
            }
            if(v[i].second == 0){
                ans += v[i].first * 1LL * mulv;
                mulh++;
            }
        }
        return ans;
    }
};
