class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cost = [ ('h',i,horizontalCut[i]) for i in range(m-1) ] + [ ('v',i,verticalCut[i]) for i in range(n-1) ]
        cost.sort(reverse = True, key = lambda x:x[2] )

        hcuts = 0 
        vcuts = 0
        res = 0
        for t,i,c in cost:
            if t == 'h':
                res += c * (vcuts+1)
                hcuts += 1
            else:
                res += c * (hcuts+1)
                vcuts += 1
        return res
