class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {

        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());

        long long int xx=0, yy=0;
        long long int ans=0;
        long long int piece1=1, piece2=1;
        while(xx<h.size() && yy<v.size())
        {
            if(h[xx]<v[yy])
            {
                ans+=v[yy]*piece1;
                piece2++;
                yy++;
            }
            else
            {
                ans+=h[xx]*piece2;
                piece1++;
                xx++;
            }
        }

        while(xx<h.size())
        {
            ans+=h[xx]*piece2;
            xx++;
        }
        while(yy<v.size())
        {
            ans+=v[yy]*piece1;
            yy++;
        }
        return ans;
    }
};
