class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(),horizontalCut.end());
        sort(verticalCut.begin(),verticalCut.end());
        long long hnum=1,vnum=1;
        long long res=0;
        while(!horizontalCut.empty()&&!verticalCut.empty()){
            if(horizontalCut.back()>=verticalCut.back()){
                res+=(long long)horizontalCut.back()*vnum;
                hnum++;
                horizontalCut.pop_back();
            }
            else{
                res+=(long long)verticalCut.back()*hnum;
                vnum++;
                verticalCut.pop_back();
            }
        }
        while(!horizontalCut.empty()){
            res+=(long long)horizontalCut.back()*vnum;
            hnum++;
            horizontalCut.pop_back();
        }
        while(!verticalCut.empty()){
            res+=(long long)verticalCut.back()*hnum;
            vnum++;
            verticalCut.pop_back();
        }
        return res;
    }
};
