class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
    sort(h.rbegin(),h.rend());
    sort(v.rbegin(),v.rend());

    int hp = 1,vp = 1;
    int i = 0,j = 0;
    long long int cost = 0;

    while (i < h.size() && j < v.size()) 
    {
        if (h[i] > v[j]) 
        {
            cost += h[i] * vp;
            hp++;
            i++;
        } 
        else 
        {
            cost += v[j] * hp;
            vp++;
            j++;
        }
    }

    while (i < h.size()) 
    {
        cost += h[i] * vp;
        hp++;
        i++;
    }

    while (j < v.size()) 
    {
        cost += v[j] * hp;
        vp++;
        j++;
    }

    return cost;
    }
};
