class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []
        for cut in horizontalCut:
            cuts.append((cut, 'H'))
        for cut in verticalCut:
            cuts.append((cut, 'V'))

        cuts.sort(reverse=True)
        h_count = v_count = 1
        total_cost = 0
        for i, cut in enumerate(cuts):
            cost, cut_type = cut
            if cut_type == 'H':
                h_count += 1
                total_cost += cost * v_count
            elif cut_type == 'V':
                v_count += 1
                total_cost += cost * h_count
        return total_cost
