class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        mer = []
        ver_count = 1
        hor_count = 1

        for i in range(m-1):
            mer.append([horizontalCut[i],0])


        for j in range(n-1):
            mer.append([verticalCut[j],1])


        mer = sorted(mer, key=lambda x: x[0], reverse=True)
        res = 0
        for i in range(len(mer)):
            val = mer[i][0]
            dire = mer[i][1]

            if dire==1:
                hor_count+=1
                res+=val*ver_count
            else:
                ver_count+=1
                res+=val*hor_count

        return res
