class Solution:
    def minimumCost(self, R: int, C: int, H: List[int], V: List[int]) -> int:
        H.sort()
        V.sort()
        h = 1
        v = 1
        ans = 0
        while H and V:
            if H[-1] > V[-1]:
                h += 1
                ans += H.pop() * v
            elif V[-1] > H[-1]:
                v += 1
                ans += V.pop() * h
            else:
                if H[-1] * v > V[-1] * h:
                    h += 1
                    ans += H.pop() * v
                else:
                    v += 1
                    ans += V.pop() * h
        while H:
            ans += H.pop() * v
        while V:
            ans += V.pop() * h
        return ans
