class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        vector<pair<int, int>> a;
        for(int i = 0;i < m-1;i++){
            a.push_back({h[i], 0});
        }
        for(int i = 0;i < n-1;i++){
            a.push_back({v[i], 1});
        }
        sort(a.begin(), a.end(), greater<pair<int,int>>());
        int x = 0, y = 0;
        for(auto& p:a){
            int l = p.first, r = p.second;
            if(r == 0){
                ans += 1LL*(y+1)*l;
                x++;
            }else{
                ans += 1LL*(x+1)*l;
                y++;
            }
        }
        return ans;
    }
};
