using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
        sort(verticalCuts.rbegin(), verticalCuts.rend());
        sort(horizontalCuts.rbegin(), horizontalCuts.rend());

        int hIndex = 0, vIndex = 0,horizontalSections = 1,verticalSections = 1;
        long long totalCost = 0;

        while (hIndex < horizontalCuts.size() && vIndex < verticalCuts.size()) {
            if (horizontalCuts[hIndex] > verticalCuts[vIndex]) {
                totalCost += horizontalCuts[hIndex] * verticalSections, horizontalSections++,hIndex++;
            } else {
                totalCost += verticalCuts[vIndex] * horizontalSections,verticalSections++,vIndex++;
            }
        }

        while (hIndex < horizontalCuts.size()) {
            totalCost += horizontalCuts[hIndex] * verticalSections,hIndex++;
        }

        while (vIndex < verticalCuts.size()) {
            totalCost += verticalCuts[vIndex] * horizontalSections,vIndex++;
        }

        return totalCost;
    }
};
