class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:


        combCut = []

        for cut in verticalCut:
            combCut.append((cut,"V"))
        for cut in horizontalCut:
            combCut.append((cut,"H"))

        combCut.sort(key = lambda x:x[0],reverse = True)

        # print(combCut)
        ans = vc = hc = 0

        for cut,dim in combCut:
            if dim == "V":
                ans += (cut*(hc+1))
                vc+=1
            else:
                ans += (cut*(vc+1))
                hc+=1

        return ans
