class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cost = 0
        costs = sorted([(c, 0) for c in horizontalCut] + [(c, 1) for c in verticalCut], reverse = True)
        count = [1, 1]
        for c, t in costs:
            cost += c * count[t]
            count[1 - t] += 1
        return cost
