class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCuts, vector<int>& vCuts) {



        sort(hCuts.rbegin(), hCuts.rend());
        sort(vCuts.rbegin(), vCuts.rend());

        int i = 0, j = 0;
        int ms1 = 1, ms2 = 1;
        long long cost = 0;

        if( hCuts.size() == 0 ){

            while (j < vCuts.size()) {
                        cost += vCuts[j] * ms1;
                        ms2++;
                        j++;
            }

                    return cost;

        }
        if( vCuts.size() == 0 ){

             while (i < hCuts.size()) {
                        cost += hCuts[i] * ms2;
                        ms1++;
                        i++;
                    }
                    return cost;

        }
        while (i < hCuts.size() && j < vCuts.size()) {

            if (hCuts[i] >= vCuts[j]) {
                cost += hCuts[i] * ms2;
                ms1++;
                i++;
            } else {
                cost += vCuts[j] * ms1;
                ms2++;
                j++;
            }

            if( i == hCuts.size() ){

                    while (j < vCuts.size()) {
                        cost += vCuts[j] * ms1;
                        ms2++;
                        j++;
                    }
                break;
            }
            if( j == vCuts.size() ){

                    while (i < hCuts.size()) {
                        cost += hCuts[i] * ms2;
                        ms1++;
                        i++;
                    }
                break;
            }

        }


        return cost;
    }
};
