class Solution {

    class Cut{
        int i;
        boolean hor;
        int cost;

        Cut(int a, boolean b, int c)
        {
            i = a;
            hor = b;
            cost = c;
        }
    }

    public long minimumCost(int r, int c, int[] horizontalCut, int[] verticalCut) {
        Cut[] cuts = new Cut[r+c-2];
        for(int i = 0; i < r-1; i++)
            cuts[i] = new Cut(i, true, horizontalCut[i]);
        for(int i = 0; i < c-1; i++)
            cuts[i+r-1] = new Cut(i, false, verticalCut[i]);
        Arrays.sort(cuts, (a, b) -> b.cost - a.cost);
        long hCuts = 0, vCuts = 0;
        long ans = 0;
        for(int i = 0; i < r+c-2; i++)
        {
            if(cuts[i].hor)
            {
                ans += (vCuts + 1) * cuts[i].cost;
                hCuts++;
            }
            else
            {
                ans += (hCuts + 1) * cuts[i].cost;
                vCuts++;
            }
        }
        return ans;
    }
}
